import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { AnimatePresence, motion } from 'framer-motion'; // Import Framer Motion
import Home from './pages/Home';
import LoginPage from './pages/LoginPage';
import AdvertiserLoginPage from './pages/AdvertiserLoginPage';
import UserAccountCreation from './pages/UserAccountCreation';
import AdvertiserAccountCreation from './pages/AdvertiserAccountCreation';
import EventDetails from './pages/EventDetails';
import Header from './components/Header';
import Footer from './components/Footer';
import './App.css';

// Animation variants for fade-and-rise effect
const fadeAndRiseVariants = {
  initial: { opacity: 0, y: 30 }, // Start transparent and slightly below
  animate: { opacity: 1, y: 0 }, // Fade in and rise up to original position
  exit: { opacity: 0, y: 30 }, // Fade out and move slightly down
};

const fadeAndRiseTransition = {
  duration: 0.5, // Smooth animation duration
  ease: 'easeOut', // Modern easing effect
};

// Wrapper for page transitions
const AnimatedPage = ({ children }) => {
  return (
    <motion.div
      variants={fadeAndRiseVariants}
      initial="initial"
      animate="animate"
      exit="exit"
      transition={fadeAndRiseTransition}
    >
      {children}
    </motion.div>
  );
};

function AnimatedRoutes() {
  const location = useLocation(); // Get the current route location

  return (
    <AnimatePresence mode="wait"> {/* Handles animations for route changes */}
      <Routes location={location} key={location.pathname}>
        <Route
          path="/"
          element={
            <AnimatedPage>
              <Home />
            </AnimatedPage>
          }
        />
        <Route
          path="/login"
          element={
            <AnimatedPage>
              <LoginPage />
            </AnimatedPage>
          }
        />
        <Route
          path="/advertiser-login"
          element={
            <AnimatedPage>
              <AdvertiserLoginPage />
            </AnimatedPage>
          }
        />
        <Route
          path="/create-user-account"
          element={
            <AnimatedPage>
              <UserAccountCreation />
            </AnimatedPage>
          }
        />
        <Route
          path="/signup"
          element={
            <AnimatedPage>
              <AdvertiserAccountCreation />
            </AnimatedPage>
          }
        />
        <Route
          path="/create-advertiser-account"
          element={
            <AnimatedPage>
              <AdvertiserAccountCreation />
            </AnimatedPage>
          }
        />
        <Route
          path="/event/:id"
          element={
            <AnimatedPage>
              <EventDetails />
            </AnimatedPage>
          }
        />
      </Routes>
    </AnimatePresence>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <main>
          <AnimatedRoutes />
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
