import React from 'react';
import { useParams } from 'react-router-dom';
import './EventDetails.css';

const events = [
  {
    id: 1,
    title: 'Ahas Yaathra Live in Colombo',
    date: '2024-11-09',
    time: '7 pm',
    venue: 'Waters Edge Outdoor',
    district: 'Colombo',
    poster: '/images/ahasyathracolombo.jpg',
    description: 'A mesmerizing live concert featuring Ahas Yaathra.',
    ticketCategories: [
      { category: 'VIP', price: '5000 LKR' },
      { category: 'Regular', price: '3000 LKR' },
    ],
    ticketLink: 'https://example.com/tickets/ahasyathra',
  },
  {
    id: 2,
    title: 'Lankan Mega Carnival',
    date: '2024-12-15',
    time: '10 am',
    venue: 'Galle Face Green',
    district: 'Colombo',
    poster: '/images/lankanmegacarnival.jpg',
    description: 'A fun-filled carnival with games, food, and entertainment.',
    ticketCategories: [
      { category: 'Adult', price: '2000 LKR' },
      { category: 'Child', price: '1000 LKR' },
    ],
    ticketLink: 'https://example.com/tickets/lankancarnival',
  },
  {
    id: 3,
    title: 'Ahas Yaathra Live in Colombo',
    date: '2024-11-09',
    time: '7 pm',
    venue: 'Waters Edge Outdoor',
    district: 'Colombo',
    poster: '/images/ahasyathracolombo.jpg',
    description: 'A mesmerizing live concert featuring Ahas Yaathra.',
    ticketCategories: [
      { category: 'VIP', price: '5000 LKR' },
      { category: 'Regular', price: '3000 LKR' },
    ],
    ticketLink: 'https://example.com/tickets/ahasyathra',
  },
  {
    id: 4,
    title: 'Lankan Mega Carnival',
    date: '2024-12-15',
    time: '10 am',
    venue: 'Galle Face Green',
    district: 'Colombo',
    poster: '/images/lankanmegacarnival.jpg',
    description: 'A fun-filled carnival with games, food, and entertainment.',
    ticketCategories: [
      { category: 'Adult', price: '2000 LKR' },
      { category: 'Child', price: '1000 LKR' },
    ],
    ticketLink: 'https://example.com/tickets/lankancarnival',
  },
  // Add more events as needed
];

function EventDetails() {
  const { id } = useParams();
  const event = events.find((event) => event.id === parseInt(id));

  if (!event) {
    return <div>Event not found</div>;
  }

  return (
    <div className="event-details">
      <div className="event-details-container">
        <img src={event.poster} alt={event.title} className="event-image" />
        <div className="event-info">
          <h1>{event.title}</h1>
          <p><strong>Date:</strong> {event.date}</p>
          <p><strong>Time:</strong> {event.time}</p>
          <p><strong>Venue:</strong> {event.venue}</p>
          <p><strong>District:</strong> {event.district}</p>
          <p>{event.description}</p>
          <h2>Ticket Categories and Prices</h2>
          <ul>
            {event.ticketCategories.map((category, index) => (
              <li key={index}>{category.category}: {category.price}</li>
            ))}
          </ul>
          <a href={event.ticketLink} target="_blank" rel="noopener noreferrer" className="ticket-link">
            Buy Tickets
          </a>
        </div>
      </div>
    </div>
  );
}

export default EventDetails;
