import React, { useState } from 'react';
import EventCard from '../components/EventCard';
import DatePicker from 'react-datepicker'; // Importing react-datepicker
import 'react-datepicker/dist/react-datepicker.css'; // Importing react-datepicker CSS
import './Home.css'; // Add relevant CSS file for grid layout and filters

const events = [
  {
    id: 1,
    title: 'Ahas Yaathra Live in Colombo',
    date: '2024-11-09', // ISO format for better date handling
    time: '7 pm',
    venue: 'Waters Edge Outdoor',
    district: 'Colombo',
    poster: '/images/ahasyathracolombo.jpg',
  },
  {
    id: 2,
    title: 'Lankan Mega Carnival',
    date: '2024-12-15',
    time: '10 am',
    venue: 'Galle Face Green',
    district: 'Colombo',
    poster: '/images/lankanmegacarnival.jpg',
  },
  {
    id: 3,
    title: 'Kandy Esala Perahera',
    date: '2024-08-10',
    time: '6 pm',
    venue: 'Kandy City Center',
    district: 'Kandy',
    poster: '/images/kandyperahera.jpg',
  },
  {
    id: 4,
    title: 'Jaffna Music Festival',
    date: '2024-09-20',
    time: '5 pm',
    venue: 'Jaffna Fort',
    district: 'Jaffna',
    poster: '/images/jaffnamusicfestival.jpg',
  },
  {
    id: 5,
    title: 'Ahas Yaathra Live in Colombo',
    date: '2024-11-09', // ISO format for better date handling
    time: '7 pm',
    venue: 'Waters Edge Outdoor',
    district: 'Colombo',
    poster: '/images/ahasyathracolombo.jpg',
  },
  {
    id: 6,
    title: 'Lankan Mega Carnival',
    date: '2024-12-15',
    time: '10 am',
    venue: 'Galle Face Green',
    district: 'Colombo',
    poster: '/images/lankanmegacarnival.jpg',
  },
  // Add more events as needed
];

const districts = [
  'Ampara', 'Anuradhapura', 'Badulla', 'Batticaloa', 'Colombo', 'Galle', 'Gampaha', 'Hambantota', 'Jaffna', 'Kalutara', 'Kandy', 'Kegalle', 'Kilinochchi', 'Kurunegala', 'Mannar', 'Matale', 'Matara', 'Monaragala', 'Mullaitivu', 'Nuwara Eliya', 'Polonnaruwa', 'Puttalam', 'Ratnapura', 'Trincomalee', 'Vavuniya'
];

function Home() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedDistrict, setSelectedDistrict] = useState('');

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleDistrictChange = (event) => {
    setSelectedDistrict(event.target.value);
  };

  const clearFilters = () => {
    setSelectedDate(null);
    setSelectedDistrict('');
  };

  return (
    <div className="home-page">
      <div className="search-bar">
        <input type="text" placeholder="Search events..." />
      </div>
      <div className="filter-section">
        <h2>Filter Events</h2>
        <div className="filter-date">
          <label htmlFor="date-picker">Select Date:</label>
          <DatePicker
            id="date-picker"
            selected={selectedDate}
            onChange={handleDateChange}
            dateFormat="yyyy-MM-dd"
            placeholderText="Select a date"
          />
        </div>
        <div className="filter-district">
          <label htmlFor="district-select">Select District:</label>
          <select
            id="district-select"
            value={selectedDistrict}
            onChange={handleDistrictChange}
          >
            <option value="">All Districts</option>
            {districts.map((district) => (
              <option key={district} value={district}>{district}</option>
            ))}
          </select>
        </div>
        <button className="clear-filters-btn" onClick={clearFilters}>
          Clear Filters
        </button>
      </div>
      <div className="event-grid">
        {events
          .filter((event) => {
            if (selectedDate && event.date !== selectedDate.toISOString().split('T')[0]) {
              return false;
            }
            if (selectedDistrict && event.district !== selectedDistrict) {
              return false;
            }
            return true;
          })
          .map((event) => (
            <EventCard key={event.id} event={event} />
          ))}
      </div>
    </div>
  );
}

export default Home;
